import { useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");

  const fetchOdds = async () => {
    if (!name.trim()) {
      setError("Please enter a player name");
      setStats(null);
      return;
    }
    try {
      const res = await fetch(`http://localhost:3001/player-odds?name=${name}`);
      const data = await res.json();
      if (data.error) {
        setError(data.error);
        setStats(null);
      } else {
        setStats(data);
        setError("");
      }
    } catch (err) {
      setError("Something went wrong");
      setStats(null);
    }
  };

  // Determine fantasy rating meter color & label
  const getRatingColor = (points) => {
    if (points >= 80) return "#22c55e"; // green
    if (points >= 50) return "#eab308"; // yellow
    return "#ef4444"; // red
  };

  return (
    <div className="app-container">
      <h1 className="title">‚öΩ Player Fantasy Points Predictor</h1>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter player name"
        className="input"
      />
      <button onClick={fetchOdds} className="button">
        Get Fantasy Points
      </button>
      {error && <p className="error">{error}</p>}
      {stats && !error && (
        <div className="card">
          <div className="player-header">
            <img
              src={stats.photo}
              alt={stats.name}
              className="player-photo"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src =
                  "https://via.placeholder.com/80?text=No+Image";
              }}
            />
            <div>
              <p>
                <strong>üë§ Name:</strong> {stats.name}
              </p>
              <p>
                <strong>üèüÔ∏è Team:</strong> {stats.team}
              </p>
              <p>
                <strong>üéØ Position:</strong> {stats.position}
              </p>
            </div>
          </div>
          <p>
            <strong>üìä Goals per Game:</strong> {stats.odds}
          </p>
          <p>
            <strong>‚≠ê Fantasy Points:</strong> {stats.fantasyPoints}
          </p>

          <div className="rating-meter">
            <div
              className="rating-fill"
              style={{
                width: `${Math.min(stats.fantasyPoints, 100)}%`,
                backgroundColor: getRatingColor(stats.fantasyPoints),
              }}
            ></div>
            <span className="rating-label">
              {stats.fantasyPoints >= 80
                ? "Excellent Fantasy Pick!"
                : stats.fantasyPoints >= 50
                ? "Good Fantasy Pick"
                : "Low Fantasy Rating"}
            </span>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
