import { useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");

  const fetchOdds = async () => {
    try {
      const res = await fetch(`http://localhost:3001/player-odds?name=${name}`);
      const data = await res.json();
      if (data.error) {
        setError(data.error);
        setStats(null);
      } else {
        setStats(data);
        setError("");
      }
    } catch (err) {
      setError("Something went wrong");
    }
  };

  const getRecommendation = (points) => {
    if (points >= 75) return "üî• Excellent Investment";
    if (points >= 50) return "üëç Good Pick";
    if (points >= 30) return "ü§î Risky Pick";
    return "‚ùå Not Recommended";
  };

  return (
    <div className="app-container">
      <h1 className="title">‚öΩ Player Fantasy Points Predictor</h1>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter player name"
        className="input"
      />
      <button onClick={fetchOdds} className="button">
        Get Fantasy Points
      </button>
      {error && <p className="error">{error}</p>}
      {stats && !error && (
        <div className="card">
          <img src={stats.photo} alt={stats.name} className="player-photo" />
          <p><strong>üë§ Name:</strong> {stats.name}</p>
          <p><strong>üèüÔ∏è Team:</strong> {stats.team}</p>
          <p><strong>üìä Goals per Game:</strong> {stats.odds}</p>
          <p><strong>‚≠ê Fantasy Points:</strong> {stats.fantasyPoints}</p>
          <p><strong>üìà Recommendation:</strong> {getRecommendation(stats.fantasyPoints)}</p>
        </div>
      )}
    </div>
  );
}

export default App;
